(function(){tinymce.create('tinymce.plugins.CodefightMediaPlugin',{init:function(ed,url){ed.addCommand('mceCodefightMedia',function(){if(ed.dom.getAttrib(ed.selection.getNode(),'class').indexOf('mceItem')!=-1)return;ed.windowManager.open({file:url+'/image.htm',width:480+parseInt(ed.getLang('codefightmedia.delta_width',0)),height:385+parseInt(ed.getLang('codefightmedia.delta_height',0)),inline:1},{plugin_url:url});});ed.addButton('image',{title:'codefightmedia.image_desc',cmd:'mceCodefightMedia'});},getInfo:function(){return{longname:'Codefight Media',author:'Codefight CMS',authorurl:'http://codefight.org/',infourl:'http://codefight.org/',version:tinymce.majorVersion+"."+tinymce.minorVersion};}});tinymce.PluginManager.add('codefightmedia',tinymce.plugins.CodefightMediaPlugin);})();